import { useState } from "react";
import "./ExpenseForm.css";

function ExpenseForm() {
  const [enteredExpenseTitle, setEnteredExpenseTitle] = useState();
  const titleChange = (event) => {
    setEnteredExpenseTitle(event.target.value);
  };
  const [enteredAmount, setEnteredAmount] = useState();
  const amountChange = (event) => {
    setEnteredExpenseTitle(event.target.value);
  };
  const [enteredDate, setEnteredDate] = useState();
  const dateChange = (event) => {
    setEnteredExpenseTitle(event.target.value);
  };
  
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChange} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" onChange={amountChange}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2018-01-01" max="2022-12-31" onChange={dateChange}/>
        </div>
      </div>
    </form>
  );
}

export default ExpenseForm;
